class Solution {
public:
    bool ok(vector<int>& arr, int t)
    {
        if (t == 0)
        {
            return true;
        }
        vector<int> dp(t + 1);
        dp[0] = 1;

        for (int i = 0; i < arr.size(); i++)
        {
            for (int j = t; j >= 0; j--)
            {
                if (dp[j] == 0)
                {
                    continue;
                }
                int nv = arr[i] + j;
                if (nv <= t)
                {
                    dp[nv] = 1;
                }
            }
            if (dp[t] == 1)
            {
                return true;
            }
        }
        return false;
    }

    bool solve(vector<int>& nums, vector<vector<int>>& queries, int t)
    {
        for (int i = 0; i < nums.size(); i++)
        {
            vector<int> arr;
            for (int j = 0; j <= t; j++)
            {
                int l = queries[j][0];
                int r = queries[j][1];
                int v = queries[j][2];
                if (i >= l && i <= r)
                {
                    arr.push_back(v);
                }
            }
            if (!ok(arr, nums[i]))
            {
                return false;
            }
        }
        return true;
    }

    bool allzero(vector<int>& nums)
    {
        for (auto& num : nums)
        {
            if (num != 0)
            {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        int l = 0;
        int r = m - 1;

        if (allzero(nums))
        {
            return 0;
        }

        while (l < r)
        {
            int mid = (l + r) / 2;

            if (solve(nums, queries, mid))
            {
                r = mid;
            }
            else
            {
                l = mid + 1;
            }
        }

        if (solve(nums, queries, l))
        {
            return l + 1;
        }
        return -1;
    }
};

