import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;
        boolean alreadyZero = true;
        for (int num : nums) {
            if (num != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero) return 0;

        BitSet[] dp = new BitSet[n];
        for (int i = 0; i < n; i++) {
            dp[i] = new BitSet(nums[i] + 1);
            dp[i].set(0);
        }

        for (int k = 0; k < q; k++) {
            int l = queries[k][0], r = queries[k][1], val = queries[k][2];
            for (int i = l; i <= r; i++) {
                BitSet shifted = shiftLeft(dp[i], val, nums[i]);
                dp[i].or(shifted);
            }
            boolean allZero = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i].get(nums[i])) {
                    allZero = false;
                    break;
                }
            }
            if (allZero) return k + 1;
        }
        return -1;
    }

    private BitSet shiftLeft(BitSet bs, int shift, int target) {
        BitSet res = new BitSet(target + 1);
        for (int i = bs.nextSetBit(0); i >= 0 && i <= target; i = bs.nextSetBit(i + 1)) {
            if (i + shift <= target) {
                res.set(i + shift);
            }
        }
        return res;
    }
}

