class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int left = 0, right = queries.size();
        int k = INT_MAX;

        while(left <= right){
            int mid = left + (right - left) / 2;
            if(isZeroArray(nums, queries, mid)){
                k = mid;
                right = mid - 1;
            }
            else{
                left = mid + 1;
            }
        }

        return k == INT_MAX ? -1 : k;
    }
private:
    bool subsetSumHelper(int num, vector<int>& vals){
        if(num == 0) return true;
        if(vals.empty()) return false;

        vector<bool> dp(num + 1, false);
        dp[0] = true;
        for(auto& val : vals){
            for(int i = num; i >= val; i--){
                dp[i] = dp[i] || dp[i - val];
            }
        }
        return dp[num];
    }

    bool isZeroArray(vector<int>& nums, vector<vector<int>>& queries, int k){
        int n = nums.size();
        vector<int> diff_arr(n + 1, 0);
        vector<vector<int>> vals_arr(n);

        for(int i_k = 0; i_k < k; i_k++){
            for(int i = queries[i_k][0]; i <= queries[i_k][1]; i++){
                vals_arr[i].push_back(queries[i_k][2]);
            }
        }

        for(int i = 0; i < n; i++){
            if(subsetSumHelper(nums[i], vals_arr[i]) == false) return false;
        }

        return true;
    }
};

