class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if not nums: return -1

        if all(e == 0 for e in nums): return 0
        n = len(nums)
        # dp[i] is a bitmask, its value is 2 ^ (nums[i] + 1) (totally we need nums[i] bits) based on values in queries
        dp = [1] * n  # .......1 (value = 0 (index 0)) is achievable

        masks = [(1 << (num + 1)) - 1 for num in nums]

        for idx, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                # how can we deal with a subset of indices?
                dp[i] |= (dp[i] << val)  # magic, it will shift existing indices to val positions 2, 5, ...
                dp[i] &= masks[i]


            if all((dp[i] >> nums[i]) & 1 for i in range(n)):
                return idx + 1

        return -1

