class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, q = queries.length;

        int minReq = -1;
        for (int i = 0; i < n; i++) {
            int[][] dp = new int[nums[i] + 1][q];
            for (int[] row : dp) Arrays.fill(row, -1);

            int currReq = minQueries(nums[i], 0, i, queries, dp);
            if (currReq == Integer.MAX_VALUE) {
                return -1;
            }

            minReq = Math.max(minReq, currReq);
        }

        return minReq;

    }

    private int minQueries(int num, int i, int idx, int[][] queries, int[][] dp) {
        if (num == 0) {
            return i;
        }

        if (i == queries.length) {
            return Integer.MAX_VALUE;
        }

        if (dp[num][i] != -1) return dp[num][i];

        int[] query = queries[i];
        int l = query[0], r = query[1], val = query[2];

        int notUse = minQueries(num, i + 1, idx, queries, dp);

        int use = Integer.MAX_VALUE;
        if (idx >= l && idx <= r && num - val >= 0) {
            use = minQueries(num - val, i + 1, idx, queries, dp);
        }

        return dp[num][i] = Math.min(use, notUse);
    }
}

