class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = new boolean[n][1001];
        for(int j = 0 ; j < n ; j++){
            Arrays.fill(dp[j],false);
            dp[j][0] = true;
        }
        boolean allZero = true;
        for(int num : nums){
            if(num!= 0){
                allZero = false;
                break;
            }
        }
        if(allZero) return 0;
        for(int k = 0 ; k < queries.length ; k++){
            int l = queries[k][0];
            int r = queries[k][1];
            int x = queries[k][2];

            for(int j = l ; j <= r; j++){
                for(int pos = 1000; pos>=x ; pos--){
                    dp[j][pos] |= dp[j][pos-x];
                }
                for(int pos = nums[j]+1;pos<=1000; pos++){
                    dp[j][pos] = false;
                }
            }
            boolean allDone = true;
            for(int j = 0 ; j < n ; j++){
                if(!dp[j][nums[j]]){
                    allDone = false;
                    break;
                }
            }
            if(allDone) return k+1;
        }
        return -1;
    }
}

