class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int length = nums.length;
        boolean[][] dp = new boolean[length][1001];
        for (int i = 0; i < length; i++) {
            dp[i][0] = true;
        }

        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        for (int queryIndex = 0; queryIndex < queries.length; queryIndex++) {
            int left = queries[queryIndex][0];
            int right = queries[queryIndex][1];
            int value = queries[queryIndex][2];

            for (int index = left; index <= right; index++) {
                boolean[] updated = new boolean[1001];

                for (int sum = 0; sum < 1001 - value; sum++) {
                    if (dp[index][sum]) {
                        updated[sum + value] = true;
                    }
                }

                for (int sum = 0; sum < 1001; sum++) {
                    dp[index][sum] |= updated[sum];
                }

                for (int sum = nums[index] + 1; sum < 1001; sum++) {
                    dp[index][sum] = false;
                }
            }

            boolean allAchievable = true;
            for (int i = 0; i < length; i++) {
                if (!dp[i][nums[i]]) {
                    allAchievable = false;
                    break;
                }
            }

            if (allAchievable) return queryIndex + 1;
        }

        return -1;
    }
}

