class Solution {
    public int minZeroArray(int[] arr, int[][] updates) {
        int len = arr.length;
        boolean[][] dpState = new boolean[len][1001];

        for (int i = 0; i < len; i++) {
            Arrays.fill(dpState[i], false);
            dpState[i][0] = true;
        }

        boolean isAllZero = true;
        for (int num : arr) {
            if (num != 0) {
                isAllZero = false;
                break;
            }
        }
        if (isAllZero) return 0;

        for (int step = 0; step < updates.length; step++) {
            int left = updates[step][0];
            int right = updates[step][1];
            int value = updates[step][2];

            for (int i = left; i <= right; i++) {
                boolean[] newState = new boolean[1001];

                for (int pos = 0; pos < 1001; pos++) {
                    if (dpState[i][pos]) {
                        newState[pos] = true;
                        if (pos + value < 1001) {
                            newState[pos + value] = true;
                        }
                    }
                }

                dpState[i] = newState;

                for (int pos = arr[i] + 1; pos < 1001; pos++) {
                    dpState[i][pos] = false;
                }
            }

            boolean isValid = true;
            for (int i = 0; i < len; i++) {
                if (!dpState[i][arr[i]]) {
                    isValid = false;
                    break;
                }
            }

            if (isValid) return step + 1;
        }
        return -1;
    }
}

