import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] dp = new boolean[n][1001];

        // Initialize dp array
        for (int j = 0; j < n; j++) {
            Arrays.fill(dp[j], false);
            dp[j][0] = true;  // Base case: 0 can always be achieved
        }

        // Check if all elements are already zero
        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        // Process each query
        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            // Update DP for range [l, r]
            for (int j = l; j <= r; j++) {
                boolean[] newDp = new boolean[1001];

                for (int pos = 0; pos <= 1000; pos++) {
                    if (dp[j][pos]) {
                        newDp[pos] = true; // Keep previous values
                        if (pos + v <= 1000) {
                            newDp[pos + v] = true; // Add `v` to existing values
                        }
                    }
                }

                // Reset values beyond nums[j]
                for (int pos = nums[j] + 1; pos <= 1000; pos++) {
                    newDp[pos] = false;
                }

                dp[j] = newDp; // Update dp state
            }

            // Check if all numbers in nums can be achieved
            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j][nums[j]]) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;  // Return query index (1-based)
        }

        return -1; // If no query makes all numbers reachable
    }

}

