class Solution {
public:
    bool subsetSumToK(int n, int k, vector<int>& arr) {
        vector<bool> prev(k + 1, false), curr(k + 1, false);
        prev[0] = true;
        curr[0] = true;
        if(n > 0 && arr[0] <= k)
            prev[arr[0]] = true;
        for (int i = 1; i < n; i++) {
            curr[0] = true;
            for (int j = 1; j <= k; j++) {
                if (j >= arr[i])
                    curr[j] = (prev[j] || prev[j - arr[i]]);
                else
                    curr[j] = prev[j];
            }
            prev = curr;
        }
        return prev[k];
    }

    bool f(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> contributions(n);
        for (int i = 0; i < k; i++) {
            int L = queries[i][0], R = queries[i][1], val = queries[i][2];
            for (int j = L; j <= R; j++) {
                contributions[j].push_back(val);
            }
        }
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                if (contributions[i].empty()) return false;
                if (!subsetSumToK(contributions[i].size(), nums[i], contributions[i]))
                    return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        bool allZero = true;
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        int Q = queries.size();
        if (!f(Q, nums, queries))
            return -1;
        int low = 0, high = Q;
        while (low < high) {
            int mid = low + (high - low) / 2;
            if (f(mid, nums, queries))
                high = mid;
            else
                low = mid + 1;
        }
        return low;
    }
};

