class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(); // n is only of size 10
        vector<vector<bool>> arr(n);
        for (int i = 0; i < n; i++) {
            vector<bool> curr(nums[i] + 1, false);
            curr[0] = true;
            arr[i] = curr;
        }

        int count = 0; // how many 0 elems
        int countQueries = 1;
        int k;
        for (auto it : nums) {
            if (it == 0) {
                count++;
            }
        }
        if (count == n) {
            return 0;
        }
        for (auto it : queries) {
            for (int i = it[0]; i <= it[1]; i++) {
                if (arr[i][nums[i]]) continue;

                // now we do the array stuff
                k = it[2];
                int l = nums[i];
                for (int j = l; j >= 0; j--) {
                    if (k + j <= nums[i] && arr[i][j]) {
                        arr[i][j + k] = true;
                    }
                }
                if (arr[i][nums[i]]) {
                    count++;
                }
            }
            if (count == n) {
                return countQueries;
            }
            countQueries++;
        }

        return -1;
    }
};

