class Solution {
public:

    bool isSubsetSum(vector<int> &arr, int sum) {
    int n = arr.size();

    // Create a 2D vector for storing results
      // of subproblems
    vector<vector<bool>> dp(n + 1, vector<bool>(sum + 1, false));

    // If sum is 0, then answer is true (empty subset)
    for (int i = 0; i <= n; i++)
        dp[i][0] = true;

    // Fill the dp table in bottom-up manner
    for (int i = 1; i <= n; i++) {

        for (int j = 1; j <= sum; j++) {
            if (j < arr[i - 1]) {

               // Exclude the current element
                dp[i][j] = dp[i - 1][j]; 
            }
            else {

               // Include or exclude
                dp[i][j] = dp[i - 1][j] 
                || dp[i - 1][j - arr[i - 1]];
            }
        }
    }

    return dp[n][sum];
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int s=0,e=queries.size();
        int n=nums.size();
        int ans=-1;
        while(s<=e){
            int m=(e+s)/2;

            vector<vector<int>>v(n);

            for(int i=0;i<n;i++){
               for(int j = 0; j < m; j++){
                   auto it = queries[j];
                   if(i >= it[0] && i <= it[1])   v[i].push_back(it[2]);
               }

            }

             for(int i=0;i<n;i++){
                if(!isSubsetSum(v[i],nums[i]) ){
                    s=m+1;
                    break;
                }

                 if(i==n-1){
                     ans=m;
                     e=m-1;
                 }
             }


        }


        return ans;

    }
};

