class Solution {
    int f(int num, int i, int idx, vector<vector<int>>& q, vector<vector<int>>& dp){
        if(num == 0){
            return i;
        }
        if(i >= q.size()){
            return q.size() + 1;
        }
        if(dp[num][i] >= 0){
            return dp[num][i];
        }
        int firstKQuery = q.size() + 1;
        int l = q[i][0], r = q[i][1], val = q[i][2];
        if(l <= idx && idx <= r && val <= num){
            firstKQuery = min(firstKQuery, f(num - val, i + 1, idx, q, dp));
        }
        firstKQuery = min(firstKQuery, f(num, i + 1, idx, q, dp));
        dp[num][i] = firstKQuery;
        return firstKQuery;
    }
    int minFirstKQuery(int idx, int num, vector<vector<int>>& queries){
        int q = queries.size();
        //f(val, i, idx, q) = min(f(val - q[i][2], i + 1, idx, q), f(val, i + 1, idx, q))
        vector<vector<int>> dp(num + 1, vector<int>(q, -1));
        return f(num, 0, idx, queries, dp);
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int k = 0;
        for(int i = 0, firstKQuery = 0; i < n; i++){
            firstKQuery = minFirstKQuery(i, nums[i], queries);
            if(firstKQuery > queries.size())
                return -1;
            k = max(k, firstKQuery);
        }
        return k;
    }
};

