class Solution {
    private static boolean isAllZeroes(int[] nums) {
        for(int num : nums) {
            if (num != 0) return false;
        }
        return true;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        if(isAllZeroes(nums)) return 0;

        int n = nums.length;
        int maxValue = 1000;
        boolean[][] dp = new boolean[n][maxValue + 1];

        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }

        if (Arrays.stream(nums).allMatch(num -> num == 0)) {
            return 0;
        }

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];

            for (int i = l; i <= r; i++) {
                for (int sum = maxValue - v; sum >= 0; sum--) {
                    if (dp[i][sum]) {
                        dp[i][sum + v] = true;
                    }
                }

                Arrays.fill(dp[i], nums[i] + 1, maxValue + 1, false);
            }

            boolean allAchieved = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i][nums[i]]) {
                    allAchieved = false;
                    break;
                }
            }

            if (allAchieved) return k + 1;
        }

        return -1;
    }
}

