class Solution {
    public int compute(int i, int amount, ArrayList<Integer> list, int[][] dp){
        if(amount == 0) return i;

        if(i == list.size() || amount<0) return list.size()+1;
        if(dp[i][amount] != -1) return dp[i][amount];

        int with = compute(i+1, amount-list.get(i), list, dp);
        int without = compute(i+1, amount, list, dp);

        return dp[i][amount] = Math.min(with, without);
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        ArrayList<ArrayList<Integer>> list = new ArrayList<>();
        for(int i = 0; i<nums.length; i++){
            list.add(new ArrayList<Integer>());
        }

        for(int[] query : queries){
            int s = query[0];
            int e = query[1];
            for(int i = 0; i<nums.length; i++){
                if(i>=s && i<=e){
                    list.get(i).add(query[2]);
                }
                else{
                    list.get(i).add(0);
                }
            }
        }

        int max = -1;
        for(int i = 0; i<nums.length; i++){
            int amount = nums[i];
            ArrayList<Integer> temp = list.get(i);

            int[][] dp = new int[temp.size()][amount+1];

            for(int[] arr : dp){
                Arrays.fill(arr, -1);
            }
            int val = compute(0, amount, temp, dp);
            // System.out.println(amount + " " + val);
            if(val > temp.size()) return -1;

            max = Math.max(max, val);
        }

        return max;
    }
}

