class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& qrs) {
        map<int, vector<int>> q_map;

        int n = nums.size(), q = qrs.size();

        int l = 0, r = q, res = -1;
        while (l <= r) {
            int m = l + (r - l) / 2;
            q_map.clear();

            for (int i = 0; i < m; ++i) {
                int li = qrs[i][0], ri = qrs[i][1], v = qrs[i][2];
                for (int j = li; j <= ri; ++j) {
                    q_map[j].push_back(v);
                }
            }

            if (canMakeZero(nums, q_map, n)) {
                res = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return res;
    }

private:
    bool canMakeZero(vector<int>& nums, map<int, vector<int>>& q_map, int n) {
        for (int i = 0; i < n; ++i) {
            if (!isSubsetSumPossible(q_map[i], nums[i])) {
                return false;
            }
        }
        return true;
    }

    bool isSubsetSumPossible(vector<int>& vals, int tgt) {
        int m = vals.size();
        vector<bool> dp(tgt + 1, false);
        dp[0] = true;

        for (int v : vals) {
            for (int j = tgt; j >= v; --j) {
                dp[j] = dp[j] || dp[j - v];
            }
        }
        return dp[tgt];
    }
};

