class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> min_time(n, INT_MAX);
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) {
                min_time[i] = 0;
                continue;
            }

            unordered_set<int> showed;
            showed.insert(nums[i]);
            for (int j = 0; j < queries.size(); j++) {
                auto& q = queries[j];
                int l = q[0];
                int r = q[1];
                int v = q[2];
                unordered_set<int> next_showed = showed;
                if (i >= l && i <= r) {
                    for (int prev_show : showed) {
                        next_showed.insert(prev_show-v);
                    }
                }


                if (next_showed.count(0)) {
                    min_time[i] = j+1;
                    break;
                }
                swap(showed, next_showed);
            }
        }
        int k = -1;
        for (int t : min_time) {
            k = max(k, t);
        }
        if (k == INT_MAX)
            return -1;
        return k;
    }
};

