class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans=0;
        for(int i=0;i<nums.length;i++){
            if(nums[i]==0){
                continue;
            }
            Integer[][] dp=new Integer[queries.length][10001];
            int curr=helper(nums[i],queries,0,i,dp);
            if(curr==Integer.MAX_VALUE){
                return -1;
            }
            else{
                ans=Math.max(ans,curr);
            }
        }
        return ans;
    }
    public int helper(int sum,int[][] queries,int pos,int index,Integer[][] dp){
        if(sum==0){
            return pos;
        }
        if(pos>=queries.length){
            return Integer.MAX_VALUE;
        }
        if(dp[pos][sum]!=null){
            return dp[pos][sum];
        }
        int take=Integer.MAX_VALUE;;
        if(queries[pos][0]<=index&&queries[pos][1]>=index&&sum>=queries[pos][2]){
            take=helper(sum-queries[pos][2],queries,pos+1,index,dp);
        }
        int noTake=helper(sum,queries,pos+1,index,dp);
        return dp[pos][sum]=Math.min(take,noTake);
    }
}

