func minZeroArray(nums []int, qs [][]int) int {
	res := 0
	for i := range nums {
		if nums[i] <= 0 {
			continue
		}
		cur := len(qs) + 1
		hm := make(map[int]bool)
		hm[0] = true
		for j, q := range qs {
			if q[2] > nums[i] || q[0] > i || q[1] < i {
				continue
			}
			if hm[nums[i]-q[2]] {
				cur = j + 1
				break
			}
			cur := make(map[int]bool)
            cur[0] = true
			for sum := range hm {
				if q[2]+sum < nums[i] {
					cur[q[2]+sum] = true
				}
			}
            for sum := range cur {
                hm[sum] = true
            }
		}

		if cur == len(qs)+1 {
			return -1
		}
		res = max(res, cur)
	}

	return res
}

