class Solution {

public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();

        vector<unordered_set<int>> dp(n);

        for (int j = 0; j < n; j++) {

            dp[j].insert(0);

        }

        bool allzero = true;

        for (int num : nums) {

            if (num != 0) {

                allzero = false;

                break;

            }

        }

        if (allzero) return 0;

        for (int k = 0; k < queries.size(); k++) {

            int l = queries[k][0];

            int r = queries[k][1];

            int v = queries[k][2];

            for (int j = l; j <= r; j++) {

                unordered_set<int> newDp = dp[j];

                for (int sum : dp[j]) {

                    newDp.insert(sum + v);

                }

                dp[j] = move(newDp);

                for (auto it = dp[j].begin(); it != dp[j].end();) {

                    if (*it > nums[j]) {

                        it = dp[j].erase(it);

                    } else {

                        ++it;

                    }

                }

            }

            bool allAchieved = true;

            for (int j = 0; j < n; j++) {

                if (dp[j].find(nums[j]) == dp[j].end()) {

                    allAchieved = false;

                    break;

                }

            }

            if (allAchieved) return k + 1;

        }

        return -1;

    }

};

