class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        using ll = long long;
        auto isPos = [&](vector<ll> &A, ll val)->bool {
            vector<vector<ll>> dp((ll)A.size()+1, vector<ll>(val+1,-1));
            auto rec = [&](const auto &self, ll i, ll rem)->ll {
                  if(i == (ll)A.size()) {
                      return rem == 0;
                  }  
                if(dp[i][rem] != -1)
                    return dp[i][rem];

                ll notTake = self(self,i+1,rem);
                ll take = 0;
                if(rem-A[i] >= 0) {
                    take = self(self,i+1,rem-A[i]);
                }

                return dp[i][rem] = (notTake or take);
            };

            return rec(rec,0,val);
        };

        auto isOk = [&](ll k)->bool {
            ll n = (ll)nums.size();
            vector<vector<ll>> bag(n);

            for(ll i = 0; i < k; i++) {
                ll l = queries[i][0];
                ll r = queries[i][1];
                ll val = queries[i][2];

                for(ll j = l; j <= r; j++) {
                    bag[j].push_back(val);
                }
            }

            // Obtain nums[i] using elements from bag[i]
            bool flag = true;
            for(int i = 0; i < n; i++){
                flag &= isPos(bag[i],nums[i]);
            }

            return flag;
        };

        ll l = -1, r = queries.size()+1;
        while(r-l>1) {
            ll mid = l + (r-l) / 2;
            if(isOk(mid))
                r = mid;
            else 
                l = mid;
        }

        if(r == (ll)queries.size()+1)
            r = -1;

        return r;
    }
};

