class Solution {
    public function minZeroArray($nums, $queries) {
        $n = count($nums);
        $dp = array_fill(0, $n, array_fill(0, 1001, 0));

        for ($i = 0; $i < $n; $i++) {
            $dp[$i][0] = 1;
        }

        $allZero = true;
        for ($i = 0; $i < $n; $i++) {
            if ($nums[$i] != 0) {
                $allZero = false;
                break;
            }
        }

        if ($allZero) {
            return 0;
        }

        foreach ($queries as $index => $query) {
            list($l, $r, $shift) = $query;

            for ($i = $l; $i <= $r; $i++) {
                for ($j = 1000; $j >= 0; $j--) {
                    if ($dp[$i][$j] == 1) {
                        if ($j + $shift <= 1000) {
                            $dp[$i][$j + $shift] = 1;
                        }
                    }
                }

                for ($pos = $nums[$i] + 1; $pos < 1001; $pos++) {
                    $dp[$i][$pos] = 0;
                }
            }

            $achieved = true;
            for ($i = 0; $i < $n; $i++) {
                if ($dp[$i][$nums[$i]] != 1) {
                    $achieved = false;
                    break;
                }
            }

            if ($achieved) {
                return $index + 1;
            }
        }

        return -1;
    }
}

