class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        d=[0]*n
        for i in range(n):
            d[i]=1<<0
        if all(x == 0 for x in nums):
            return 0
        for k, (l, r, v) in enumerate(queries):
            for j in range(l, r + 1):
                sh = 0
                for i in range(1001 - v):
                    if d[j] & (1 << i):
                        sh |= (1 << (i + v))

                d[j]=d[j]|sh
                for p in range(nums[j]+1,1001):
                    o=~(1<<p)
                    d[j]&=o
            flag = True

            for j in range(n):
                if not (d[j] & (1 << nums[j])):
                    flag = False
                    break
