class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;

        boolean alreadyZero = true;
        for (int val : nums) {
            if (val != 0) {
                alreadyZero = false;
                break;
            }
        }
        if (alreadyZero) {
            return 0;
        }

        int maxSum = 1000;
        boolean[][] dp = new boolean[n][maxSum + 1];

        for (int i = 0; i < n; i++) {
            dp[i][0] = true;
        }

        for (int q = 0; q < queries.length; q++) {
            int left  = queries[q][0];
            int right = queries[q][1];
            int val   = queries[q][2];

            for (int i = left; i <= right; i++) {
                for (int sum = maxSum; sum >= val; sum--) {
                    if (dp[i][sum - val]) {
                        dp[i][sum] = true;
                    }
                }
            }

            boolean allZeroPossible = true;
            for (int i = 0; i < n; i++) {
                int needed = nums[i];
                if (needed > maxSum || !dp[i][needed]) {
                    allZeroPossible = false;
                    break;
                }
            }
            if (allZeroPossible) {
                return q + 1;
            }
        }

        return -1;
    }
}

