class Solution {
public:
    vector<vector<int>> dp;

    bool rec(vector<int>& v, int t, int idx) {
        if(t == 0) return true;
        if(idx == v.size()) return false;
        if(dp[idx][t] != -1) return dp[idx][t] == 1;
        bool notTake = rec(v, t, idx + 1);
        bool take = false;
        if(v[idx] <= t)
            take = rec(v, t - v[idx], idx + 1);
        dp[idx][t] = (take || notTake) ? 1 : 0;
        return (take || notTake);
    }

    bool solve(vector<int>& v, int t) {
        int n = v.size();
        dp.assign(n + 1, vector<int>(t + 1, -1));
        return rec(v, t, 0);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = nums.size();
        int m = q.size();
        bool alreadyZero = true;
        for (int x : nums)
            if (x != 0) { alreadyZero = false; break; }
        if (alreadyZero)
            return 0;
        for (int k = 0; k < m; k++) {
            bool flag = true;
            for (int i = 0; i < n; i++) {
                if (nums[i] == 0)
                    continue;
                vector<int> vals;
                for (int j = 0; j <= k; j++) {
                    if (q[j][0] <= i && i <= q[j][1])
                        vals.push_back(q[j][2]);
                }
                if (!solve(vals, nums[i])) {
                    flag = false;
                    break;
                }
            }
            if (flag)
                return k + 1;
        }
        return -1;
    }
};

