class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        List<Set<Integer>> numList = new ArrayList<>();
        Set<Integer> isZero = new HashSet<>();
        for (int i = 0; i < nums.length; i++) {
            numList.add(new HashSet<>());
            numList.get(i).add(nums[i]);
            if (nums[i] == 0) {
                isZero.add(i);
            }
        }
        if (isZero.size() == nums.length) return 0;
        for (int i = 0; i < queries.length; i++) {
            for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                if (isZero.contains(j)) continue;
                Set<Integer> currentSet = numList.get(j);
                Set<Integer> nextSet = new HashSet<>(currentSet);
                for (Integer s: currentSet) {
                    if (s > queries[i][2]) {
                        nextSet.add(s - queries[i][2]);
                    } else if (s == queries[i][2]) {
                        nextSet.add(0);
                        isZero.add(j);
                        break;
                    }
                }
                numList.set(j, nextSet);
            }
            if (isZero.size() == nums.length) {
                return i + 1;
            }
        }
        return -1;
    }
}

