class Solution {
    public int minZeroArray(int[] a, int[][] queries) {
        int n = a.length, k = -1;
        for (int i = 0; i < n; i++) {
            Integer dp[][] = new Integer[a[i] + 1][queries.length + 1];
            int t = f(queries, a[i], i, 0, dp);
            if (t > queries.length) return -1;
            k = Math.max(k, t);
        }
        return k;
    }
    private int f(int[][] q, int a, int ind, int i, Integer dp[][]) {
        int n = q.length;
        if (a == 0) return i;
        if (a < 0) return n + 1;
        if (i >= n) return n + 1;
        if (dp[a][i] != null) return dp[a][i];
        int take = n + 1;
        if (q[i][0] <= ind && q[i][1] >= ind) {
            take = f(q, a - q[i][2], ind, i + 1, dp);
        }
        int not = f(q, a, ind, i + 1, dp);
        return dp[a][i] = Math.min(take, not);
    }
}

