class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        d = defaultdict(list)
        for i, (l, r, v) in enumerate(queries):
            for j in range(l, r+1):
                d[j].append((i, v))

        def go(arr, target):
            n = len(arr)
            dp = [False] * (target + 1)
            dp[0] = True 
            for k in range(n):
                for i in range(target, arr[k][1] - 1, -1):
                    if dp[i - arr[k][1]]:
                        dp[i] = True
                        if dp[target]:
                            return arr[k][0] + 1        
            return -1

        res = 0
        # for k,v in d.items():
        #     print(k,v)
        for i in range(len(nums)):
            if not nums[i]:
                continue
            if i not in d and nums[i]:
                return -1
            if i not in d:
                return -1
            tmp = go(d[i], nums[i])
            if tmp == -1:
                return -1
            res = max(res, tmp)
        return res

