class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(); 
        int m = queries.size(); 
        int lo = 0, hi = m;  
        int ans = m + 1;

        while (hi >= lo) {  
            int mid = lo + (hi - lo) / 2;  
            vector<int> pref(n, 0); 
            bool ok = true;
            for (int i = 0; i < mid; ++i) {  
                int a = queries[i][0]; 
                int b = queries[i][1]; 
                pref[a] += queries[i][2]; 
                if (b + 1 < n)
                    pref[b + 1] -= queries[i][2];
            } 

            for (int i = 1; i < n; ++i)
                pref[i] += pref[i - 1];

            for (int i = 0; i < n; ++i) { 
                pref[i] -= nums[i];  
                if (pref[i] < 0) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                for (int i = 0; i < n; ++i) {
                    if (pref[i] == 0) 
                        continue;

                    vector<int> allowed;
                    for (int j = 0; j < mid; ++j) {  
                        if (queries[j][0] <= i && queries[j][1] >= i) {
                            allowed.push_back(queries[j][2]);
                        }
                    }

                    if (allowed.empty()) {
                        ok = false; 
                        break; 
                    }

                    int target = abs(pref[i]);
                    vector<bool> dp(target + 1, false);
                    dp[0] = true; 

                    for (int num : allowed) {
                        for (int s = target; s >= num; --s) {
                            if (dp[s - num]) {
                                dp[s] = true;
                            }
                        }
                    } 

                    if (!dp[target]) {
                        ok = false;
                        break;
                    }
                }
            }

            if (ok) {  
                ans = min(ans, mid); 
                hi = mid - 1;
            } else {  
                lo = mid + 1;
            }
        } 

        return (ans == m + 1 ? -1 : ans);
    }
};

