class Solution {
public:
    unordered_map<string, int> dp_map;

    string hashKey(vector<int>& nums, int it) {
        string key = to_string(it) + "#";
        for (int num : nums) key += to_string(num) + ",";
        return key;
    }

    int rec(vector<int> nums, vector<vector<int>>& queries, int it, vector<bitset<1001>>& bit_mask) {
        if (it >= queries.size()) return INT_MAX;

        string key = hashKey(nums, it);
        if (dp_map.find(key) != dp_map.end()) return dp_map[key];

        int l = queries[it][0], r = queries[it][1], v = queries[it][2];

        vector<int> temp = nums;
        for (int j = l; j <= r; j++) {
            bit_mask[j] |= (bit_mask[j] << v);
            for (int pos = nums[j] + 1; pos < 1001; pos++) {
                bit_mask[j].reset(pos);
            }
        }

        bool allAchieved = true;
        for (int j = 0; j < nums.size(); j++) {
            if (!bit_mask[j].test(nums[j])) {
                allAchieved = false;
                break;
            }
        }

        if (allAchieved) return dp_map[key] = it;

        int x = rec(temp, queries, it + 1, bit_mask);
        int y = rec(nums, queries, it + 1, bit_mask);

        return dp_map[key] = min(x, y);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> bit_mask(n);  
        for (int j = 0; j < n; j++) bit_mask[j].set(0, true);

        bool allZero = true;
        for (int num : nums) if (num > 0) allZero = false;
        if (allZero) return 0;

        int ans = rec(nums, queries, 0, bit_mask);
        return (ans == INT_MAX) ? -1 : ans + 1;
    }
};

