class Solution {
    public int minZeroArray(int[] elements, int[][] modifications) {
        int length = elements.length;
     List<BitSet> countes = new ArrayList<>(length);
        List<int[]> diff = Arrays.asList(modifications);

        int l = 0;
        while (l < length) {
            BitSet bitSet = new BitSet(1001);
            bitSet.set(0);
            countes.add(bitSet);
            l++;
        }

        boolean isHasZero = true;
        int curr = 0;
        while (curr < length) {
            if (elements[curr] != 0) {
                isHasZero = false;
                break;
            }
            curr++;
        }

        if (isHasZero)
            return 0;

        int mods = 0;
        while (mods < diff.size()) {
            int[] change = diff.get(mods);
            int start = change[0];
            int end = change[1];
            int decrement = change[2];

            int pos = start;
            while (pos <= end) {
                countes.get(pos).or(moveBits(countes.get(pos), decrement));
                int reset = elements[pos] + 1;
                while (reset < 1001) {
                    countes.get(pos).clear(reset);
                    reset++;
                }
                pos++;
            }

            boolean isChanged = true;
            int counted = 0;
            while (counted < length) {
                if (!countes.get(counted).get(elements[counted])) {
                    isChanged = false;
                    break;
                }
                counted++;
            }

            if (isChanged)
                return mods + 1;
            mods++;
        }

        return -1;
    }

    private BitSet moveBits(BitSet bitSet, int shiftAmount) {
        BitSet shiftedSet = new BitSet(1001);
        for (int pos = bitSet.nextSetBit(0); pos >= 0; pos = bitSet.nextSetBit(pos + 1)) {
            if (pos + shiftAmount < 1001) {
                shiftedSet.set(pos + shiftAmount);
            }
        }
        return shiftedSet;
    }
}

