class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int q = queries.length;
        int[][] dp = new int[n][q+5];

        for(int i =0; i<q; i++){
            int[] query = queries[i];
            int l = query[0], r = query[1], val = query[2];
            for(int j = l; j<=r; j++){
                dp[j][i] = val; // storing 0 based index for querying.
            }
        }

        // now for each index, we want the min time in which we can turn it to 0;
        int[] ans = new int[n];
        int[][] temp = new int[q+5][1010];
        for(int i=0; i<n; i++){
            // we want to turn nums[i] into zero
            ans[i] = findMinQueryForIdx(i, nums, dp, temp);
        }
        int res = -1;
        for(int i=0; i<n; i++){
            if(ans[i] == -1){
                return -1;
            }else{
                res = Math.max(res, ans[i]);
            }
        }
        return res;
    }

    private int findMinQueryForIdx(int i, int[] nums, int[][] dp, int[][] temp){
        // this returns the min no of steps into which nums[i] can be turned into zero.
        int target = nums[i];
        if(target == 0){
            return 0;
        }
        for(int[] r: temp){
            Arrays.fill(r, -1);
        }
        int[] row= dp[i];
        int steps = solve(0, target, row, row.length, temp);

        if(steps >= (int)1e9){
            return -1;
        }
        return steps;
    }

    int solve(int i, int target, int[] row, int n, int[][] temp){
        // this can be optimized using a dp
        if(target == 0){
            return 0;
        }
        if(i == n){
            return (int)1e9;
        }
        if(temp[i][target] != -1){
            return temp[i][target];
        }

        int notPick = 1+solve(i+1, target, row, n, temp);
        int pick = (int)1e9;
        if(row[i] <= target){
            pick = 1+solve(i+1, target-row[i], row, n, temp);
        }
        return temp[i][target] = Math.min(pick, notPick);
    }



}

