class Solution {
public:
    unordered_map<string, bool> memo;

    bool solve(vector<int>& nums, vector<vector<vector<int>>>& idx, int k) {
        int n = nums.size();
        string key = to_string(k) + "|" + addKey(nums, n);
        if (memo.count(key)) return memo[key];

        for (int i = 0; i < nums.size(); i++) {
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (const auto& op : idx[i]) {
                if (op[0] < k) {
                    int val = op[1];
                    for (int j = target; j >= val; j--) {
                        if (dp[j - val]) {
                            dp[j] = true;
                        }
                    }
                }
            }
            if (!dp[target]) return memo[key] = false;
        }
        return memo[key] = true;
    }

    string addKey(vector<int>& nums, int n) {
        string res;
        for (int i = 0; i < n; i++) res += to_string(nums[i]) + ",";
        return res;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        bool flag = true;
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                flag = false;
                break;
            }
        }
        if (flag) return 0;

        vector<vector<vector<int>>> idx(n);
        for (int i = 0; i < queries.size(); i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                idx[j].push_back({i, val});
            }
        }

        int s = 1, e = queries.size(), ans = -1;
        while (s <= e) {
            int mid = s + (e - s) / 2;
            if (solve(nums, idx, mid)) {
                ans = mid;
                e = mid - 1;
            } else {
                s = mid + 1;
            }
        }
        return ans;
    }
};

