class Solution {
public:

    bool f(int i, int k, vector<int>& candidates, vector<vector<int>>& dp) {
        if (k == 0)
            return true;
        if (i >= candidates.size() || k < 0)
            return false;
        if (dp[i][k] != -1)
            return dp[i][k];

        bool include = f(i + 1, k - candidates[i], candidates, dp);
        bool exclude = f(i + 1, k, candidates, dp);
        return dp[i][k] = include || exclude;
    }

    bool check(int mid, vector<int>& arr, vector<vector<int>>& queries) {
        int n = arr.size();
        // e[i] will store all query values that affect index i.
        vector<vector<int>> e(n);
        for (int i = 0; i < mid; i++) {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; j++) {
                e[j].push_back(val);
            }
        }

        for (int i = 0; i < n; i++) {
            int target = arr[i];

            if (target == 0)
                continue;

            if (e[i].empty())
                return false;

            vector<vector<int>> dp(e[i].size() + 1, vector<int>(target + 1, -1));
            if (!f(0, target, e[i], dp))
                return false;
        }
        return true;
    }


    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size();
        int ans = -1;
        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (check(mid, nums, queries)) {
                ans = mid;
                r = mid - 1;  // try to find a smaller mid
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

