class Solution {
public:
    bool allElementsZero(const vector<int>& arr) {
        for (int num : arr) {
            if (num != 0) return false;
        }
        return true;
    }

    void initializeDP(vector<bitset<1001>>& dp, int size) {
        for (int i = 0; i < size; i++) {
            dp[i].reset();
            dp[i].set(0);
        }
    }

    bool allTargetsAchieved(const vector<bitset<1001>>& dp, const vector<int>& nums) {
        for (int i = 0; i < nums.size(); i++) {
            if (!dp[i].test(nums[i])) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& arr, vector<vector<int>>& operations) {
        int n = arr.size();
        vector<bitset<1001>> dp(n);

        initializeDP(dp, n);

        if (allElementsZero(arr)) return 0;

        for (int opIdx = 0; opIdx < operations.size(); opIdx++) {
            int left = operations[opIdx][0];
            int right = operations[opIdx][1];
            int value = operations[opIdx][2];

            for (int i = left; i <= right; i++) {
                dp[i] |= (dp[i] << value);
                for (int pos = arr[i] + 1; pos < 1001; pos++) {
                    dp[i].reset(pos);
                }
            }

            if (allTargetsAchieved(dp, arr)) {
                return opIdx + 1;
            }
        }

        return -1;
    }
    // int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

    // }
};

