class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ans = 0
        for i in range(len(nums)):
            if nums[i] == 0:
                continue
            b = 1 << nums[i]
            res = -1
            for j in range(len(queries)):
                if queries[j][0] <= i and i <= queries[j][1]:
                    b = b | (b >> queries[j][2])
                if b & 1:
                    res = j
                    break
            if res == -1:
                return res
            ans = max(ans, res + 1)
        return ans

