class Solution {
public:
    int minZeroArray(vector<int>& num, vector<vector<int>>& q) {
        int res = 0, n = num.size(), m = q.size();
        for (int p = 0; p < n; p++) {
            int i = num[p];
            if (!i) continue;
            vector<int> dp(i + 1);
            dp[0] = 1;
            for (int j = 0; j < m; j++) {
                int l = q[j][0], r = q[j][1], w = q[j][2];
                if (p < l or p > r) continue;
                for (int k = i - w; k >= 0; k--)
                    dp[k + w] |= dp[k];
                if (dp[i]) {
                    res = max(res, j + 1);
                    break;
                }
            }
            if (!dp[i]) return -1;
        }
        return res;
    }
};

