class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all([t==0 for t in nums]) :
            return 0
        snums = [set([t]) for t in nums] 

        for it, (li, ri, val) in enumerate(queries) :
            for i in range(li, ri+1) :
                if 0 in snums[i] :
                    continue
                new_set = set()
                for t in snums[i] :
                    if t - val >= 0 :
                        new_set.add(t-val)
                snums[i] |= new_set

            if all([0 in t for t in snums]) :
                return it+1
        return -1

