const int inf = 0x3f3f3f3f;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), ans = 0;
        for(int i = 0; i < n; ++i) {
            vector<int> f(nums[i] + 1, +inf);
            f[0] = 0;
            for(int j = 0; j < m; ++j) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];
                if(l <= i && i <= r) {
                    for(int k = nums[i]; k >= val; --k) {
                        if(f[k - val] < +inf) {
                            f[k] = min(f[k], j + 1);
                        }
                    }
                }
            }
            ans = max(ans, f[nums[i]]);
        }
        return ans == +inf ? -1 : ans;
    }
};

