class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
        int n = a.size(), m = queries.size();
        int ret = 0;
        const int N = 1e3 + 1;
        auto solve = [&](int id) {
            if (a[id] == 0) return 0;
            bitset<N> dp;
            dp[0] = 1;
            for (int i = 0; i < m; ++i) {
                int L = queries[i][0], R = queries[i][1], val = queries[i][2];
                if (id < L || id > R) continue;
                dp = dp | (dp << val);
                if (dp[a[id]]) return i + 1;
            }
            return m + 1;
        };
        for (int i = 0; i < n; ++i) {
            int cur = solve(i);
            ret = max(ret, cur);
        }
        if (ret > m) return -1;
        return ret;
    }
};

