class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def check(pos, val):
            if val == 0:
                return 0

            can = [False] * (val + 1)
            can[0] = True
            for i, (l, r, k) in enumerate(queries):
                if l <= pos <= r:
                    for z in range(val, -1, -1):
                        if can[z] and z + k <= val:
                            can[z + k] = True
                if can[val]:
                    return i + 1
            return -1

        reqs = [check(i, num) for i, num in enumerate(nums)]
        if any(req == -1 for req in reqs):
            return -1
        return max(reqs)

