const int N = 1024;
bitset<N> bs;
int solve(int v, int p, vector<vector<int>>& queries){
    if(v == 0)  return 0;
    bs.reset();
    bs.set(0);
    int n = queries.size();
    for(int i=0; i<n; ++i){
        int l = queries[i][0];
        int r = queries[i][1];
        int w = queries[i][2];
        if(p < l || p > r)  continue;
        bs |= (bs << w);
        if(bs.test(v)){
            return i + 1;
        }
    }
    return -1;
}
class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
        int ret = 0;
        int n = a.size();
        for(int i=0; i<n; ++i){
            int cur = solve(a[i], i, queries);
            if(cur == -1){
                return -1;
            }
            ret = max(ret, cur);
        }
        return ret;
    }
};

