class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def do(x):
            v = nums[x]
            lis = [1] + [0] * v
            for i in has[x]:
                for j in range(v,i-1,-1):
                    lis[j] = lis[j]|lis[j-i]
            if lis[-1] == 1:
                nhas[x] = False
        n = len(nums)
        res = 0
        has = [[] for _ in range(n)]
        nhas = [(i>0) for i in nums]
        if max(nums) == 0: return 0
        for i,j,k in queries:
            for a in range(i,j+1):
                if nhas[a] and k <= nums[a]:
                    has[a].append(k)
                    do(a)
            res += 1
            if not any(nhas): return res
        return -1

