class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();
        int ans = 0;

        for (int idx = 0; idx < n; idx++) {
            int x = nums[idx];
            if (x == 0) continue;

            bool f[x + 1];
            memset(f, 0, sizeof(f));
            f[0] = true;
            for (int i = 0; i < q; i++) {
                if (queries[i][0] <= idx && idx <= queries[i][1]) {
                    int y = queries[i][2];
                    for (int j = x; j >= y; j--) if (f[j - y]) f[j] = true;
                }
                if (f[x]) { ans = max(ans, i + 1); break; }
            }
            if (!f[x]) return -1;
        }
        return ans;
    }
};

