func getMin(i, num int, queries [][]int) int {
    if num == 0 { return 0 }
    f := make([]bool, num+1)
    f[0] = true
    for j, q := range queries {
        l, r, v := q[0], q[1], q[2]
        if i >= l && i <= r {
            for l := num-v; l >= 0; l-- {
                if f[l] { f[l+v] = true }
            }
        }
        if f[num] { return j+1 }
    }
    return len(queries) + 1
}

func minZeroArray(nums []int, queries [][]int) int {
    m := len(queries)
    rs := 0  
    for i, num := range nums {
        rs = max(rs, getMin(i, num, queries)) 
    }
    if rs == m + 1 { rs = -1 }
    return rs
}

