class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> v(n);
        for (int i = 0; i < n; ++i)
            v[i].set(nums[i]);

        auto check = [&]() {
            for (auto& b : v)
                if (!b[0])
                    return false;
            return true;
        };

        if (check())
            return 0;

        int ans = 0;
        for (auto& q : queries) {
            ++ans;
            for (int i = q[0]; i <= q[1]; ++i)
                v[i] = v[i] | (v[i] >> q[2]);
            if (check())
                return ans;
        }
        return -1;
    }
};

