class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int N = nums.size(), Q = queries.size();

        int L = -1, R = Q + 1;
        while (L + 1 < R) {
            int mid = (L + R) >> 1;

            bool ok = true;
            REP(i, N) {
                bitset<1001> dp;
                dp[0] = 1;
                REP(j, mid) {
                    int l = queries[j][0], r = queries[j][1], w = queries[j][2];
                    if (i >= l && i <= r) {
                        dp |= dp << w;
                    }
                }
                if (!dp[nums[i]]) {
                    ok = false;
                    break;
                }
            }
            if (ok) R = mid; else L = mid;
        }

        return R == Q + 1 ? -1 : R;
    }
};

