impl Solution {
    pub fn min_zero_array(nums: Vec<i32>, queries: Vec<Vec<i32>>) -> i32 {
        if nums.iter().all(|&x| x == 0) {
            return 0;
        }
        let n = nums.len();
        let mut dp = Vec::new();
        for i in 0..n {
            dp.push(vec![false; nums[i] as usize + 1]);
            dp[i][nums[i] as usize] = true;
        }
        for (i, query) in queries.iter().enumerate() {
            let (l, r, val) = (query[0] as usize, query[1] as usize, query[2] as usize);
            for j in l..=r {
                for k in val..=nums[j] as usize {
                    dp[j][k - val] |= dp[j][k];
                }
            }
            if dp.iter().all(|x| x[0]) {
                return i as i32 + 1;
            }
        }
        -1
    }
}

