class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if(check(nums)){
            return 0;
        }

        int n = nums.length;
        HashSet<Integer>[] sets = new HashSet[n];
        int m = queries.length;

        for(int i = 0; i < n; i++){
            sets[i] = new HashSet<>();
            sets[i].add(nums[i]);
        }


        OUTER:
        for(int i = 0; i < m; i++){
            int[] query = queries[i];

            for(int j = query[0]; j <= query[1]; j++){
                List<Integer> possible = new ArrayList<>();
                for(int k : sets[j]){
                    if(k >= query[2]){
                        possible.add(k - query[2]);
                    }
                }

                sets[j].addAll(possible);
            }

            // check
            for(HashSet<Integer> set : sets){
                if(!set.contains(0)){
                    continue OUTER;
                }
            }

            return i + 1;
        }

        return -1;
    }


    private boolean check(int[] arr){
        for(int i : arr){
            if(i > 0){
                return false;
            }
        }

        return true;
    }
}

