class Solution {
public:
    int minZeroArray(vector<int>& A, vector<vector<int>>& Q) {
        int n = A.size(), qs = Q.size();

        auto ok = [&](int m) -> bool {
            for (int i = 0; i < n; ++i) {
                int a = A[i];
                vector<bool> dp(a + 1, false);
                dp[0] = true;
                for (int j = 0; j < m; ++j) {
                    if (Q[j][0] <= i && i <= Q[j][1]) {
                        int val = Q[j][2];
                        for (int s = a; s >= val; --s) {
                            dp[s] = dp[s] || dp[s - val];
                        }
                    }
                }
                if (!dp[a])
                    return false;
            }
            return true;
        };

        int l = 0, r = qs + 1;
        while (l < r) {
            int m = l + (r - l) / 2;
            if (ok(m)) {
                r = m;
            } else {
                l = m + 1;
            }
        }
        return l <= qs && ok(l) ? l : -1;
    }
};

